package leetcode.June;

import java.util.*;

// https://leetcode.com/explore/featured/card/june-leetcoding-challenge/540/week-2-june-8th-june-14th/3355/
public class InSubsequence {

    public static void main(String[] args) {
        //s = "abc", t = "ahbgdc"
        System.out.println(isSubsequence("abc", "bhagdc"));
        System.out.println(isSubsequence("abd", "adebcccd"));
        System.out.println(isSubsequence("leeeeetcode",
        ""
        ));
    }

    public static boolean isSubsequence(String s, String t) {
        Queue<String> q = toQueue(s);

        for(int i = 0; i < t.length(); i++) {
            String c = String.valueOf(t.charAt(i));
            if(c.equals(q.peek())) {
                q.poll();
            }
        }
        return q.size() == 0;
    }

    private static Queue<String> toQueue(String s) {
        Queue<String> q = new ArrayDeque<>();

        for(int i = 0; i < s.length(); i++) {
            q.add(String.valueOf(s.charAt(i)));
        }
        return q;
    }

    //
    private static boolean betterSolution(String s, String t) {
        int index = -1;
        for (char c : s.toCharArray()){
            index = t.indexOf(c, index + 1);
            if (index == -1)
                return false;
        }
        return true;
    }
}
